import { useState } from "react"
const Example = () => {
  const numArray = [1, 2, 3, 4, 5];
  const [nums, setNums] = useState(numArray)
  const shuffle = () => {
    const newNums = [...nums]
    const value = newNums.pop()
    newNums.unshift(value)
  }
  return (
    <>
      <h1>{nums}</h1>
      <button onClick={shuffle}>Shuffle</button>
    </>
  );
};

export default Example;
