import { useReducer } from "react";

const CALC_OPTIONS = ["add", "minus", "divide", "multiply"];

// reducer関数の定義
// useReducerフックを利用して、状態管理にreducer関数を利用しています。
// このアプリでは、changeと四則演算用のアクションを定義し、それぞれのアクションに対して、状態の更新を行っています。
const reducer = (state, { type, payload }) => {
  switch (type) {
    case "change": {
      const { name, value } = payload;
      return { ...state, [name]: value };
    }
    case "add": {
      return { ...state, result: state.a + state.b };
    }
    case "minus": {
      return { ...state, result: state.a - state.b };
    }
    case "divide": {
      return { ...state, result: state.a / state.b };
    }
    case "multiply": {
      return { ...state, result: state.a * state.b };
    }
    default:
      throw new Error("operator is invalid");
  }
};

const Example = () => {
  // useStateフックを使って、初期状態をinitStateとして設定し、
  // useReducerフックを使って、現在の状態をstate、状態更新用のdispatch関数を取得しています。
  // また、calculate関数とnumChangeHandler関数を定義しています。

  const initState = {
    a: 1,
    b: 2,
    result: 3,
  };
  // reducer関数は、現在の状態と、dispatchされたアクションを受け取り、新しい状態を返す関数です。
  const [state, dispatch] = useReducer(reducer, initState);

  //この場合、e.target.valueは、選択された選択肢の値を表します。
  const calculate = (e) => {
    dispatch({ type: e.target.value });
  };

  // 数値の入力には、numChangeHandler関数が利用されます。
  const numChangeHandler = (e) => {
    dispatch({ type: 'change', payload: { name: e.target.name, value: e.target.value } });
  };
  return (
    /* 完成系のJSX */
    // <CalcProvider>
    //   <Input name="a"/>      
    //   <Input name="b" />      
    //   <Select />
    //   <Result />      
    // </CalcProvider>
    // 四則演算の種類と数値の入力用のinput要素、そして結果表示用のh3要素をJSXで記述しています。
    // また、select要素には、四則演算の種類を選択するための選択肢をCALC_OPTIONS定数から生成しています。　　　

    //  数値の入力と四則演算の選択を行うと、それに応じて結果が表示されます。

    // 四則演算の選択には、calculate関数が利用されます。
    // calculate関数は、選択された四則演算の種類をdispatchし、reducer関数で状態を更新します。
    // 結果の表示には、現在の状態のresultプロパティを表示しています。
    <>
      <div>
        a:
        <input
          type="number"
          name="a"
          value={state.a}
          onChange={numChangeHandler}
        />
      </div>
      <div>
        b:
        <input
          type="number"
          name="b"
          value={state.b}
          onChange={numChangeHandler}
        />
      </div>
      <select value={state.type} name="type" onChange={calculate}>
        {CALC_OPTIONS.map((type) => (
          <option key={type} value={type}>
            {type}
          </option>
        ))}
      </select>
      <h3>結果：{state.result}</h3>
    </>
  );
};

export default Example;
