import { useReducer } from "react";
import Input from "./components/Input";

const CALC_OPTIONS = ["add", "minus", "divide", "multiply"];

const reducer = (state, { operator, data }) => {
  switch (operator) {
    case "change": {
      const { name, value } = data;
      return { ...state, [name]: value };
    }
    case "add": {
      return { ...state, result: state.a + state.b };
    }
    case "minus": {
      return { ...state, result: state.a - state.b };
    }
    case "divide": {
      return { ...state, result: state.a / state.b };
    }
    case "multiply": {
      return { ...state, result: state.a * state.b };
    }
    default:
      throw new Error("operator is invalid");
  }
};

const Example = () => {
  // 初期ステート
  const initState = {
    a: 1,
    b: 2,
    result: 3,
    operator: "divide",
  };
  const [state, dispatch] = useReducer(reducer, initState);

  // イベントごとにdispatchを呼び出し、ステートをセットの上、reducerを呼び出す
  const calculate = (e) => {
    dispatch({ operator: e.target.value });
  };



  return (
    <>
      <Input name="a" />
      <Input name="b" />


      <select value={state.operator} name="operator" onChange={calculate}>
        {CALC_OPTIONS.map((option) => (
          <option key={option} value={option}>
            {option}
          </option>
        ))}
      </select>
      <h3>結果：{state.result}</h3>
    </>
  );
};

export default Example;
