import { useReducer } from "react";

const CALC_OPTIONS = ["add", "minus", "divide", "multiply"];

const reducer = (state, { operator, data }) => {
  switch (operator) {
    case "change": {
      const { name, value } = data;
      return { ...state, [name]: value };
    }
    case "add": {
      return { ...state, result: state.a + state.b };
    }
    case "minus": {
      return { ...state, result: state.a - state.b };
    }
    case "divide": {
      return { ...state, result: state.a / state.b };
    }
    case "multiply": {
      return { ...state, result: state.a * state.b };
    }
    default:
      throw new Error("operator is invalid");
  }
};

const Example = () => {
  // 初期ステート
  const initState = {
    a: 1,
    b: 2,
    result: 3,
    type: "minus",
  };
  const [state, dispatch] = useReducer(reducer, initState);

  // イベントごとにdispatchを呼び出し、値をセットの上、reducerを呼び出す
  const calculate = (e) => {
    dispatch({ operator: e.target.value });
  };

  const numChangeHandler = (e) => {
    dispatch({ operator: 'change', data: { name: e.target.name, value: e.target.value } });
  };


  return (
    <>
      <div>
        a:
        <input
          type="number"
          name="a"
          value={state.a}
          onChange={numChangeHandler}
        />
      </div>
      <div>
        b:
        <input
          type="number"
          name="b"
          value={state.b}
          onChange={numChangeHandler}
        />
      </div>
      <select value={state.operator} name="operator" onChange={calculate}>
        {CALC_OPTIONS.map((option) => (
          <option key={option} value={option}>
            {option}
          </option>
        ))}
      </select>
      <h3>結果：{state.result}</h3>
    </>
  );
};

export default Example;
