import { createContext, useContext, useReducer } from "react"

const TodoContext = createContext()
const TodoDispatchContext = createContext()

const todosList = [
    {
        id: 1,
        content: "店予約する",
        editing: false,
    },
    {
        id: 2,
        content: "卵買う",
        editing: false,
    },
    {
        id: 3,
        content: "郵便出す",
        editing: false,
    },
];

const todoReducer = (state, action) => {
    const deleteTodo = (id) => {
        const newTodos = todos.filter((todo) => {
            return todo.id !== id;
        });

        setTodos(newTodos);
    }

    const createTodo = (todo) => {
        setTodos([...todos, todo]);
    }

    const updateTodo = (changeTodo) => {
        const newTodos = todos.map(todo => {
            // todo.editing = changeTodo.editingのように直接書き換えるとtodosの値が変わってしまうので、
            // スプレッド構文で新しいオブジェクトを作成して、その中chageTodoの値を上書きする
            return todo.id === changeTodo.id ? { ...todo, ...changeTodo } : { ...todo }
        })
        setTodos(newTodos);
    }
}

const TodoProvider = ({ children }) => {

    const [todos, dispatch] = useReducer(todoReducer, todosList);

    return (
        <TodoContext.Provider value={todos}>
            <TodoDispatchContext.Provider value={dispatch}>
                {children}
            </TodoDispatchContext.Provider>
        </TodoContext.Provider>
    )
}

const useTodos = useContext(TodoContext);
const useDispatchTodos = useContext(TodoDispatchContext);


export { useTodos, useDispatchTodos }
